(22, '65cf01ec0bfd1', NULL, '[NEW] Updated SEO Preview, Redirection Manager, Author SEO, and More', '<p>AIOSEO 4.5.6 is live, implementing many of your suggestions! 🥳</p>\n\n<p>🔎 Improved SEO Preview - To better match Google SERPs.<br>📆 Redirect Scheduler - Schedule seasonal redirects easily.<br>📂 Import Redirects from a CSV File - Add redirects in bulk.<br>🖋️ Improved Author SEO - Improvements to the E-E-A-T addon.</p>\n\n<p>AIOSEO 4.5.6 comes packed with many other improvements to the modules you already love.&nbsp;</p>\n\n<p>Update your plugin to version 4.5.6 NOW!</p>', 'success', '[\"4-x\",\"lite\"]', 1042, NULL, '2024-02-12 22:15:51', NULL, 'Learn More', 'https://aioseo.com/new-updated-seo-preview-redirection-manager-author-seo/?utm_source=WordPress&utm_medium=plugin-notification&utm_campaign=aioseo-456-v4-x-lite', 'Get AIOSEO Pro', 'https://aioseo.com/lite-upgrade/?utm_source=WordPress&utm_medium=plugin-notification&utm_campaign=aioseo-456-v4-x-lite', 1, 0, '2024-02-16 06:34:20', '2024-03-27 13:23:02'),
(23, '65d9bfd422421', NULL, 'Static sitemap files detected', 'AIOSEO has detected static sitemap files in the root folder of your WordPress installation.\n				As long as these files are present, AIOSEO is not able to dynamically generate your sitemap.', 'error', '[\"all\"]', NULL, 'sitemap-static-files', '2024-02-24 10:07:16', NULL, 'Delete Static Files', 'http://action#sitemap/delete-static-files', NULL, NULL, 1, 0, '2024-02-24 10:07:16', '2024-02-24 10:07:42'),
(24, '65f69b32b55f1', NULL, '🍀 Flash Sale: Get Up To 70% Off AIOSEO This St. Patrick\'s Day! 🍀', '<p><strong><em>Happy St. Patrick\'s Day!</em></strong> Today\'s your lucky day because we\'re celebrating with a flash sale that\'s too good to miss.</p>\n\n<p>Score <strong><em>up to 70% off</em></strong> when you upgrade your plan <strong>TODAY ONLY</strong>!</p>\n\n<p>With this deal, there\'s no need to chase down a leprechaun to find that pot of gold. 🍯 But hurry, like a rainbow, it will disappear before you know it!</p>', 'success', '[\"4-x\",\"lite\"]', 1061, NULL, '2024-03-17 00:00:00', '2024-03-19 00:00:00', 'Get AIOSEO Pro', 'https://aioseo.com/lite-upgrade/?utm_source=WordPress&utm_medium=plugin-notification&utm_campaign=st-patricks-day-sale-v4-x-lite', NULL, NULL, 0, 0, '2024-03-17 07:26:42', '2024-03-18 09:23:46'),
(25, '65fac1bb45643', NULL, '📣 [New] Query Arg Monitoring for Improved Crawling and Indexing', '<p>We’ve revamped the Crawl Cleanup module and added a <strong><em>NEW</em></strong> <strong>Query Arg Monitor</strong>. This game-changing feature gives you greater control of your query args, resulting in more efficient crawling and indexing.</p>\n\n<p>AIOSEO 4.5.8 comes packed with many other improvements to the modules you already love.&nbsp;</p>\n\n<p>Update your plugin to version 4.5.8 NOW!</p>', 'success', '[\"4-x\",\"lite\"]', 1058, NULL, '2024-03-19 19:37:40', NULL, 'Learn More', 'https://aioseo.com/new-query-arg-monitoring-for-improved-crawling-and-indexing/?utm_source=WordPress&utm_medium=plugin-notification&utm_campaign=aioseo-458-v4-x-lite', 'Get AIOSEO Pro', 'https://aioseo.com/lite-upgrade/?utm_source=WordPress&utm_medium=plugin-notification&utm_campaign=aioseo-458-v4-x-lite', 0, 0, '2024-03-20 11:00:11', '2024-03-27 13:23:02');
